import React, { useEffect, useRef, useState, useCallback } from "react";
import { Html5Qrcode } from "html5-qrcode";

function BarcodeScanner({ onScan, loading }) {
  const [scanning, setScanning] = useState(false);
  const [starting, setStarting] = useState(false);
  const [error, setError] = useState("");
  const scannerRef = useRef(null);

  const stopScanner = useCallback(async () => {
    if (scannerRef.current) {
      try {
        const state = scannerRef.current.getState();
        if (state === 2) { // SCANNING
          await scannerRef.current.stop();
        }
        scannerRef.current.clear();
      } catch (e) {
        // ignore cleanup errors
      }
      scannerRef.current = null;
    }
    setScanning(false);
    setStarting(false);
  }, []);

  const startScanner = useCallback(async () => {
    setError("");
    setStarting(true);

    // Small delay to ensure the DOM element is rendered
    await new Promise((r) => setTimeout(r, 300));

    try {
      const el = document.getElementById("barcode-reader");
      if (!el) {
        setError("Scanner element not found. Try refreshing.");
        setStarting(false);
        return;
      }

      // Clean up any previous instance
      if (scannerRef.current) {
        try { await scannerRef.current.stop(); } catch (e) {}
        try { scannerRef.current.clear(); } catch (e) {}
      }
      el.innerHTML = "";

      const scanner = new Html5Qrcode("barcode-reader");
      scannerRef.current = scanner;

      await scanner.start(
        { facingMode: "environment" },
        {
          fps: 10,
          qrbox: { width: 280, height: 150 },
          aspectRatio: 1.5,
        },
        (decodedText) => {
          onScan(decodedText.trim());
          stopScanner();
        },
        () => {}
      );

      setScanning(true);
      setStarting(false);
    } catch (err) {
      console.error("Scanner error:", err);
      setStarting(false);
      if (err.toString().includes("NotAllowedError")) {
        setError("Camera permission denied. Please allow camera access.");
      } else if (err.toString().includes("NotFoundError")) {
        setError("No camera found on this device.");
      } else {
        setError("Failed to start camera: " + err.toString());
      }
    }
  }, [onScan, stopScanner]);

  useEffect(() => {
    return () => {
      if (scannerRef.current) {
        try { scannerRef.current.stop(); } catch (e) {}
        try { scannerRef.current.clear(); } catch (e) {}
      }
    };
  }, []);

  return (
    <div className="barcode-scanner">
      {!scanning && !starting && (
        <div className="scanner-start">
          <button
            className="start-scan-btn"
            onClick={startScanner}
            disabled={loading}
          >
            <svg viewBox="0 0 24 24" width="24" height="24" fill="none" stroke="currentColor" strokeWidth="2">
              <path d="M3 7V5a2 2 0 012-2h2" />
              <path d="M17 3h2a2 2 0 012 2v2" />
              <path d="M21 17v2a2 2 0 01-2 2h-2" />
              <path d="M7 21H5a2 2 0 01-2-2v-2" />
              <line x1="7" y1="12" x2="17" y2="12" />
              <line x1="7" y1="8" x2="13" y2="8" />
              <line x1="7" y1="16" x2="15" y2="16" />
            </svg>
            Open Camera &amp; Scan Barcode
          </button>
          <p className="scanner-hint">Point your camera at the barcode on the participant's ID card</p>
        </div>
      )}

      {starting && (
        <div className="scanner-loading">
          <span className="spinner" /> Starting camera...
        </div>
      )}

      {(scanning || starting) && (
        <div className="scanner-active">
          <div id="barcode-reader" className="scanner-viewport" />
          <button className="stop-scan-btn" onClick={stopScanner}>
            ✕ Close Scanner
          </button>
        </div>
      )}

      {error && (
        <div className="scanner-error">
          <span>⚠</span> {error}
        </div>
      )}
    </div>
  );
}

export default BarcodeScanner;
